import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-add-edit-empresas',
  templateUrl: './add-edit-empresas.component.html',
  styleUrls: ['./add-edit-empresas.component.css']
})
export class AddEditEmpresasComponent implements OnInit {

  form: FormGroup;

  constructor (private fb: FormBuilder) {

    this.form = this.fb.group({
      name: ['', Validator.required]
      
    })

   }

  ngOnInit(): void {
      
  }

}
<div class="row mt-3">
    <div class="col-log-4 offset-lg-4">
        <div class="card bg-secondary mb-3" style="max-width: 25rem;">
            <div class="card-header text-center">Agregar Usuario</div>
            <div class="card-body">
                <form action="">
                    <input type="text" class="form-control"  placeholder="posa el nom">
                    <input type="text" class="form-control mt-2"  placeholder="posa el telefono">
                    <input type="text" class="form-control mt-2"  placeholder="posa la poblacio">      
                    <input type="text" class="form-control mt-2"  placeholder="posa qui al dona de alta">   
                    <div class="row mt-2 text-center">
                        <div class="col-lg-6">
                            <button  routerLink="/"  type="button" class="btn btn-light">Volver</button>                             
                        </div>
                        <div class="col-lg-6">
                            <button type="button" class="btn btn-primary">Aceptar</button>                                            
                        </div>
                    </div>
                   
                    
                </form>
                
            </div>
          </div>        
    </div>
</div>
